import React, { Component } from 'react';
import { TweetBody,MatchingPosts,TweetBox } from './components/tweet.js'
import {PullToRefresh, PullDownContent, ReleaseContent, RefreshContent} from "react-js-pull-to-refresh";
import './App.css';
import InfiniteScroll from "react-infinite-scroll-component";

class App extends Component {
  constructor(props) {
    super(props)
    this.state={
      users:
      [ 
      ]
    }
    this.handleRefresh = this.handleRefresh.bind(this)
    this.getUser = this.getUser.bind(this)
  }

  handleRefresh() {
    return new Promise((resolve) => {
      this.getUser()
    });
  }

  componentWillMount() {
    this.getUser()
  }

  getUser() {
    fetch('https://randomuser.me/api/?results=10')
    .then(response => {
      if(response.ok) return response.json();
      throw new Error('Request failed.');
    })
    .then(data => {
      this.setState({
        users:[
          {
            name: data.results[0].name,
            image: data.results[0].picture.medium,
            tweet: data.results[0].email,
            city: data.results[0].phone
          },
          ...this.state.users,
        ]
      });
    })
    .catch(error => {
      console.log(error);
    });
  }

  render() {
    return (
      <PullToRefresh
      // pullDownContent={<PullDownContent />}
      // releaseContent={<ReleaseContent />}
      // refreshContent={<RefreshContent />}
      // pullDownThreshold={2}
      onRefresh={this.getUser}
      triggerHeight={50}
      backgroundColor='black'>
      <div className="main-body">
        {[...this.state.users].map((user, index) => {
          let name = `${user.name.first} ${user.name.last}`
          let handle = `@${user.name.first}${user.name.last}`
          let profilepic = user.image
          let tweet = user.tweet
          let city = user.city
          return(
            <TweetBox>
          <div>
            <TweetBody 
              key={index}
              name={name}
              handle={handle}
              handle_2='2'
              tweet={tweet}
              profilepic={profilepic} 
              city={city}
              />
              <div>
              <MatchingPosts
              key={index}
              profilepic={profilepic}
              handle={handle}
              tweet={tweet}
              image={profilepic}
              /> 
              </div>
          </div>
          </TweetBox>
          )
        })}      
      </div>
      <div className="main-body">
        <InfiniteScroll
          dataLength={20}
          next={this.handleRefresh}
          hasMore={true}
          loader={<h4>Loading...</h4>}
        >
          {[...this.state.users].map((user, index) => {
          let name = `${user.name.first} ${user.name.last}`
          let handle = `@${user.name.first}${user.name.last}`
          let profilepic = user.image
          let tweet = user.tweet
          let city = user.city
          return(
            <TweetBox>
          <div>
            <TweetBody 
              key={index}
              name={name}
              handle={handle}
              handle_2='2'
              tweet={tweet}
              profilepic={profilepic} 
              city={city}
              />
              <div>
              <MatchingPosts
              key={index}
              profilepic={profilepic}
              handle={handle}
              tweet={tweet}
              image={profilepic}
              /> 
              </div>
          </div>
          </TweetBox>
          )
        })}    
        </InfiniteScroll>
      </div>
      </PullToRefresh>
    );
  }
}

export default App;
